const root=document.getElementById('root');function add(msg){const d=document.createElement('div');d.className='bubble';d.textContent=msg;root.appendChild(d);setTimeout(()=>d.remove(),6000)};window.addEventListener('message',e=>{if(e.data&&e.data.listener==='message'){const m=e.data.event?.data;add(`${m.displayName}: ${m.text}`)}});if(window.SE_SETTINGS){setInterval(()=>add('nya~!'),3000);}